import React, {useMemo} from 'react'
import { useTable } from 'react-table'
import MOCK_DATA from './MOCK_DATA.json'
import './Table.css'
import { COLUMNS } from './columns'

export const Table = () => {
    const columns = useMemo(() => COLUMNS , [])
    const data = useMemo(() => MOCK_DATA, [])
    
   const tableInstance =  useTable({
        columns : columns,
        data : data
    })
    const {getTableProps , getTableBodyProps, headersGroups, rows, prepareRow} = tableInstance
    return (
        <table {...getTableBodyProps}>
            <thead> {headerGroups.map(headerGroup =>
                <tr {...headersGroups.getTableBodyProps()}>
                {
                    headersGroups.headersGroups.map(column => (
                        <th{...column.getTableBodyProps()}>
                        {column.render('Header')}
                        </th>
                    ))
                }
                </tr>
                )}
            </thead>
            <tbody {...getTableBodyProps()}>
                {
                    rows.map(row => {
                        prepareRow(row)
                        return(
                            <tr {...row.getRowProps()}>
                            {row.cells.map((cell) => {
                               <td {...row.getRowProps()}> 
                               </td>
                            })}
                             </tr>
                        )
                    })
                }
               
            </tbody>
        </table>
    )
}
